    private void formWindowClosing(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowClosing
        // TODO add your handling code here:
        if (JOptionPane.showConfirmDialog(null, "Deseja finalizar o sistema?", "Finalizar - DATAUPA", JOptionPane.YES_NO_OPTION) == JOptionPane.YES_OPTION) {
            setDefaultCloseOperation(Sistema.DISPOSE_ON_CLOSE);
        } else {
            setDefaultCloseOperation(Sistema.DO_NOTHING_ON_CLOSE);
        }
    }//GEN-LAST:event_formWindowClosing

