    private void jMenuItemPacCadastrarPacienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemPacCadastrarPacienteActionPerformed
        // TODO add your handling code here:
        try {
            // Remover todos os JInternalFrame do JDesktopPane
            jDesktop.removeAll();

            // Instanciando o frame interno
            PacienteCadastrar pacienteCadastrar = new PacienteCadastrar();

            // Formatação para remover o top left drop down (arrow)
            BasicInternalFrameUI ui = (BasicInternalFrameUI) pacienteCadastrar.getUI();
            Container north = (Container) ui.getNorthPane();
            north.remove(0);
            north.validate();
            north.repaint();

            // Adiciona o JInternalFrame ao JDesktopPane
            jDesktop.add(pacienteCadastrar);

            // Inicia o JInternalFrame Maximizado
            pacienteCadastrar.setMaximum(true);
            //frame.setSize(jDesktopPane1.getWidth(), jDesktopPane1.getHeight());

            // Carrega o JInternalFrame
            pacienteCadastrar.show();

        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_jMenuItemPacCadastrarPacienteActionPerformed

    private void jMenuItemPacListarPacientesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemPacListarPacientesActionPerformed
        // TODO add your handling code here:
        try {
            // Remover todos os JInternalFrame do JDesktopPane
            jDesktop.removeAll();

            // Instanciando o frame interno
            PacienteListar pacienteConsultar = new PacienteListar();

            // Formatação para remover o top left drop down (arrow)
            BasicInternalFrameUI ui = (BasicInternalFrameUI) pacienteConsultar.getUI();
            Container north = (Container) ui.getNorthPane();
            north.remove(0);
            north.validate();
            north.repaint();

            // Adiciona o JInternalFrame ao JDesktopPane
            jDesktop.add(pacienteConsultar);

            // Inicia o JInternalFrame Maximizado
            pacienteConsultar.setMaximum(true);
            //frame.setSize(jDesktopPane1.getWidth(), jDesktopPane1.getHeight());

            // Carrega o JInternalFrame
            pacienteConsultar.show();

        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_jMenuItemPacListarPacientesActionPerformed

